cumsum_fn <- function(x) {
  cumsum_vec <- rep(0, length(x))
  cumsum_vec[1] <- x[1]
  for (i in 2:length(x)) {
    cumsum_vec[i] <- cumsum_vec[i-1] + x[i]
  }
  return(cumsum_vec)
}

# Porownanie

x <- c(2,3,5,7,11)
cumsum_fn_result <- cumsum_fn(x)
cumsum_result <- cumsum(x)


# comparing the results
all.equal(cumsum_fn_result, cumsum_result)

