myMatrix <- function(data, nrow, ncol, byrow = FALSE, dimnames = NULL) {
  if (byrow) {
    data <- t(data)
  }
  dim(data) <- c(nrow, ncol)
  if (!is.null(dimnames)) {
    dimnames(data) <- dimnames
  }
  return(data)
}
